rvname = {y: x for x, y in names.items()}

def fnumtoname():
    print("System : ระบบค้นหาชื่อจากรหัสประจำตัวนักเรียน")
    while True:
        try:
            readable_num = int(input("System : รหัสประจำตัวนักเรียนคือ \nUser : "))
            if readable_num in names:
                print(names[readable_num])
                break
            else:
                print("System : ไม่พบข้อมูล ขอโทดคับ")

        except ValueError:
            print("System : ใส่รหัสคับ")


