def fnametoclass():
    print("System : ระบบค้นหาห้องและเลขที่ จากชื่อ")
    while True:
        try:
            searchnum = str(input("System : ชื่อ \nUser : "))
            if searchnum in switched_dict:
                print("System : " + searchnum + "อยู่ห้อง" + str(switched_dict[searchnum])[:-1]+ "เลขที่ "+str(switched_dict[searchnum])[:-2])
                break
            else:
                print("System : ไม่พบข้อมูล ขอโทดคับ")


        except ValueError:
            print("System : ใส่ชื่อคับ")


