def fnametonum():
    print("System : ระบบหารหัสจากชื่อ")

    while True:
        try:
            balls = str(input("System : ใส่ชื่อจริงที่เขียนถูกต้อง ทั้งชื่อและนามสกุล \nUser : "))
            if balls in rvname:
                print(rvname[balls])

                break
            elif balls == "ภัทรภร เวกวิหารี":
                print("System : เราไม่บุลลี่คนงับ ไม่พูดถึง")
            elif balls == "วรินทร ถวิลวิทู":
                print("System : ลาพักอยู่่ ToT ToT")
            else:
                print("System : ไม่เจอชื่องับ")

        except ValueError:
            print("System : ใส่ชื่อคับ")
